var UrlApi = "https://www.api-canek.cetcom.edu.gt/api/";
function menu(){
    var settings = {
        "url": UrlApi + "MenuUsuario",
        "method": "POST",  
        "timeout": 0,
        "headers": {
          "Content-Type": "application/json"
        },
        "data": JSON.stringify({
            "IdModulo": 1,
            "TxtToken": sessionStorage.getItem("inicio") 
        }),
      };
      
      $.ajax(settings).done(function (response) {
        // console.log(response);
        
        $.each(response, function(index, data)
        {
          if(data.IdMenu == '1')
          {
            var Catalogos = "<a class='nav-link collapsed' href='#' data-toggle='collapse' data-target='#collapseUtilities' aria-expanded='true' aria-controls='collapseUtilities'>"+
            "<i class='"+ data.TxtImagen +"'></i><span>"+ data.TxtNombre +"</span></a>";
          }
            $(Catalogos).appendTo("#Catalogos");
            
          if(data.IdMenuPadre == '1')
          {
            var SubMenus = "<a class='collapse-item' href='"+ data.TxtLink + ".html' class='nav-link'>"+
            "<i class='"+ data.TxtImagen +"'></i<span>"+ data.TxtNombre +"</span></a>";
          }
            $(SubMenus).appendTo("#MenusCatalogos");

            if(data.IdMenu == '11')
            {
              var Catalogos2 = "<a class='nav-link collapsed' href='#' data-toggle='collapse' data-target='#collapseTwo' aria-expanded='true' aria-controls='collapseTwo'>"+
              "<i class='"+ data.TxtImagen +"'></i><span>"+ data.TxtNombre +"</span></a>";
            }
              $(Catalogos2).appendTo("#Catalogos2");

              if(data.IdMenuPadre == '11')
              {
                var SubMenus2 = "<a class='collapse-item' href='"+ data.TxtLink + ".html' class='nav-link'>"+
                "<i class='"+ data.TxtImagen +"'></i<span>"+ data.TxtNombre +"</span></a>";
              }
                $(SubMenus2).appendTo("#MenusCatalogos2")

                console.log(data);
       });
      
      });
    }
